import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';
import {enGB} from 'date-fns/locale';
import 'chartjs-chart-financial'; // if you want to use candlestick chart

const CandlestickChart = ({ data }) => {
  const labels = Object.keys(data['Time Series (15min)']);

  const candleData = labels.map(label => {
    const candle = data['Time Series (15min)'][label];
    return {
      t: new Date(label),
      o: parseFloat(candle['1. open']),
      h: parseFloat(candle['2. high']),
      l: parseFloat(candle['3. low']),
      c: parseFloat(candle['4. close'])
    };
  });

  const dataset = {
    datasets: [{
      label: 'Candlestick Chart',
      data: candleData,
      type: 'candlestick' // if you want to use candlestick chart
    }]
  };

  const options = {
    scales: {
      x: {
        type: 'time',
        adapters: {
          date: {
            locale: enGB,
          },
        },
      }
    }
  };

  return (
    <div style={{height:500, width:500}}>
      <Line data={dataset} options={options} />
    </div>
  );
};

export default CandlestickChart;
